mapper.update:
update sale_order
set

    payState=#{payState}


    , orderState=#{orderState}

where orderNum=#{num} and payState <>2;
update sale_suborder
set

    payState=#{payState}


    , orderState=#{orderState}

where orderNum=#{num} and payState <>2;

update sale_suborder
set

    payState=#{payState}


    , orderState=#{orderState}

where serialNum=#{num} and payState <>2;

mapper.select:
SELECT
SUM( price ) AS sale,
( SELECT count( 1 ) FROM sale_suborder

    ( payState =1 and orderState =1 ) and tenantId=#{tenantId}

      and  creationTime >= #{start}


        and creationTime <= #{end}

 ) as count,
( SELECT COUNT( 1 ) FROM sale_device  where  tenantId=#{tenantId}
) AS device,
( SELECT COUNT( 1 ) FROM sale_log


        creationTime >= #{start}


        and creationTime <= #{end}


) AS error,
( SELECT COUNT( 1 ) FROM sale_order

    ( payState =2 OR orderState =- 1 ) and tenantId=#{tenantId}

        and creationTime >= #{start}


        and creationTime <= #{end}


) AS off
FROM
sale_order a


        a.creationTime >= #{start}


        and a.creationTime <= #{end}

SELECT DATE_FORMAT( creationTime, '%h' ) as time, COUNT( 1 ) as count FROM sale_suborder
payState = 1 AND orderState = 1
and creationTime >= #{start}
and creationTime <= #{end}
GROUP BY DATE_FORMAT( creationTime, '%h' ) ORDER BY DATE_FORMAT( creationTime, '%h' )

SELECT DATE_FORMAT( creationTime, '%d' ) as time, COUNT( 1 ) as count FROM sale_suborder
payState = 1 AND orderState = 1
and creationTime >= #{start}
and creationTime <= #{end}
GROUP BY DATE_FORMAT( creationTime, '%d' ) ORDER BY DATE_FORMAT( creationTime, '%d' )

select pointName as time,COUNT(1) as count from sale_order
payState = 1 AND orderState = 1
and creationTime >= #{start}
and creationTime <= #{end}
GROUP BY pointName ORDER BY COUNT(1) desc LIMIT 0,10

SELECT payType time, COUNT( 1 ) as count FROM sale_order
payState = 1 AND orderState = 1
and creationTime >= #{start}
and creationTime <= #{end}
GROUP BY payType

SELECT productId, productName, COUNT( 1 ) saleCount, SUM( a.price ) saleMoney FROM sale_suborder a WHERE a.payState = 1 AND a.orderState = 1
and a.creationTime >= #{start}
and a.creationTime <= #{end}
and a.productName like '%${productName}%'
GROUP BY a.productId, a.productName
